import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";

const Register = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState(""); // Added phone state
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState(""); // Added confirm password state
  const [role, setRole] = useState("customer"); // Default role
  const [companyName, setCompanyName] = useState("");
  const [contactPerson, setContactPerson] = useState("");
  const [jobTitle, setJobTitle] = useState("");
  const [department, setDepartment] = useState("");
  const [shippingAddress, setShippingAddress] = useState("");
  const [billingAddress, setBillingAddress] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [passwordMatch, setPasswordMatch] = useState(true); // Added password match state
  const [phoneValid, setPhoneValid] = useState(true); // Phone validation state
  const navigate = useNavigate();

  // Check password match on change
  const handleConfirmPasswordChange = (e) => {
    const value = e.target.value;
    setConfirmPassword(value);
    setPasswordMatch(password === value);
  };

  // Phone validation
  const handlePhoneChange = (e) => {
    const value = e.target.value;
    setPhone(value);

    // Regex to allow +94712345678 OR 0712345678
    const phoneRegex = /^(\+94\d{9}|0\d{9})$/;
    setPhoneValid(phoneRegex.test(value));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    // Validate password match before submitting
    if (password !== confirmPassword) {
      setError("Passwords do not match");
      setPasswordMatch(false);
      setLoading(false);
      return;
    }

    // Validate phone number
    if (!phoneValid) {
      setError("Invalid phone number format");
      setLoading(false);
      return;
    }

    try {
      // Prepare user data based on role
      const userData = {
        name,
        email,
        phone, // Added phone
        password,
        confirmPassword, // Added confirm password
        role,
        ...(role === "supplier" && { companyName, contactPerson }),
        ...(role === "employee" && { jobTitle, department }),
        ...(role === "customer" && { shippingAddress, billingAddress }),
      };

      // Send registration request to the backend
      const response = await axios.post("http://localhost:5000/api/auth/register", userData);

      if (response.data.success) {
        alert("Registration successful! Please log in.");
        navigate("/login");
      } else {
        setError(response.data.message);
      }
    } catch (error) {
      console.error("Registration error:", error);
      setError(error.response?.data?.message || "Registration failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-amber-50 to-orange-100 p-4">
      <div className="bg-white rounded-xl shadow-xl p-8 w-full max-w-md">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-amber-700">Create an Account</h1>
          <p className="text-gray-600 mt-2">Join SpiceSense today</p>
        </div>

        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Name */}
          <div className="space-y-2">
            <label htmlFor="name" className="block text-sm font-medium text-gray-700">
              Full Name
            </label>
            <input
              id="name"
              type="text"
              placeholder="Your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-amber-500"
            />
          </div>

          {/* Phone Number - With Validation */}
          <div className="space-y-2">
            <label htmlFor="phone" className="block text-sm font-medium text-gray-700">
              Phone Number
            </label>
            <input
              id="phone"
              type="tel"
              placeholder="+94712345678 or 0712345678"
              value={phone}
              onChange={handlePhoneChange}
              required
              className={`w-full px-3 py-2 border ${
                phoneValid ? "border-gray-300" : "border-red-500"
              } rounded-md focus:outline-none focus:ring-2 ${
                phoneValid ? "focus:ring-amber-500" : "focus:ring-red-500"
              }`}
            />
            {!phoneValid && (
              <p className="text-red-500 text-xs mt-1">Invalid phone number format</p>
            )}
          </div>

          {/* Password */}
          <div className="space-y-2">
            <label htmlFor="password" className="block text-sm font-medium text-gray-700">
              Password
            </label>
            <input
              id="password"
              type="password"
              placeholder="••••••••"
              value={password}
              onChange={(e) => {
                setPassword(e.target.value);
                setPasswordMatch(e.target.value === confirmPassword);
              }}
              required
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-amber-500"
            />
          </div>

          {/* Confirm Password */}
          <div className="space-y-2">
            <label htmlFor="confirmPassword" className="block text-sm font-medium text-gray-700">
              Confirm Password
            </label>
            <input
              id="confirmPassword"
              type="password"
              placeholder="••••••••"
              value={confirmPassword}
              onChange={handleConfirmPasswordChange}
              required
              className={`w-full px-3 py-2 border ${
                passwordMatch ? "border-gray-300" : "border-red-500"
              } rounded-md focus:outline-none focus:ring-2 ${
                passwordMatch ? "focus:ring-amber-500" : "focus:ring-red-500"
              }`}
            />
            {!passwordMatch && (
              <p className="text-red-500 text-xs mt-1">Passwords do not match</p>
            )}
          </div>

          {/* Submit Button */}
          <button
            type="submit"
            disabled={loading || !passwordMatch || !phoneValid}
            className="w-full bg-amber-600 text-white py-2 px-4 rounded-md hover:bg-amber-700 transition-colors disabled:bg-amber-300"
          >
            {loading ? "Registering..." : "Register"}
          </button>
        </form>

        <div className="mt-6 text-center">
          <p className="text-sm text-gray-600">
            Already have an account?{" "}
            <Link to="/login" className="text-amber-600 hover:text-amber-800 font-medium">
              Log in
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Register;
