import React, { useState, useEffect } from "react";
import axios from "axios";

const OrdersList = () => {
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [savedCards, setSavedCards] = useState([]);
  const [selectedCard, setSelectedCard] = useState("");
  
  useEffect(() => {
    fetchOrders();
    fetchSavedCards();
  }, []);

  const fetchOrders = async () => {
    try {
      const response = await axios.get("http://localhost:5000/orders");
      setOrders(response.data);
    } catch (error) {
      console.error("Error fetching orders:", error);
    }
  };

  const fetchSavedCards = async () => {
    try {
      const response = await axios.get("http://localhost:5000/saved-cards");
      setSavedCards(response.data);
    } catch (error) {
      console.error("Error fetching saved cards:", error);
    }
  };

  const handlePayNow = (order) => {
    setSelectedOrder(order);
    setShowPaymentModal(true);
  };

  const handleConfirmPayment = async () => {
    if (!selectedOrder || !selectedCard) return;
    try {
      await axios.post("http://localhost:5000/pay", {
        orderId: selectedOrder._id,
        cardId: selectedCard,
      });
      setShowPaymentModal(false);
      fetchOrders();
    } catch (error) {
      console.error("Payment failed:", error);
    }
  };

  return (
    <div>
      <h2>My Orders</h2>
      <ul>
        {orders.map((order) => (
          <li key={order._id}>
            Order {order.orderNumber} - ${order.totalPrice} - Status: {order.status}
            {order.status === "Pending" && (
              <button onClick={() => handlePayNow(order)}>Pay Now</button>
            )}
          </li>
        ))}
      </ul>

      {showPaymentModal && (
        <div className="modal">
          <h3>Confirm Payment</h3>
          <p>Amount: ${selectedOrder?.totalPrice}</p>
          <label>Select Credit Card:</label>
          <select onChange={(e) => setSelectedCard(e.target.value)}>
            <option value="">Select...</option>
            {savedCards.map((card) => (
              <option key={card._id} value={card._id}>{card.cardNumber}</option>
            ))}
          </select>
          <button onClick={handleConfirmPayment}>Confirm Payment</button>
          <button onClick={() => setShowPaymentModal(false)}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default OrdersList;
