import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const OrderConfirmationPage = () => {
  const { orderId } = useParams();
  const [order, setOrder] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:5000/api/order/${orderId}`)
      .then((res) => setOrder(res.data))
      .catch(console.error);
  }, [orderId]);

  const handleEdit = () => setIsEditing(true);

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5000/api/order/${orderId}`, {
        items: order.items.map(item => ({
          itemId: item.itemId._id, 
          quantity: item.quantity 
        })),
        shippingAddress: order.shippingAddress,
        billingAddress: order.billingAddress
      });
      setIsEditing(false);
    } catch (error) {
      console.error("Error updating order:", error);
    }
  };

  const handleCancel = async () => {
    try {
      await axios.delete(`http://localhost:5000/api/order/${orderId}`);
      navigate('/home'); // Redirect after deletion
    } catch (error) {
      console.error("Error deleting order:", error);
    }
  };

  const handleQuantityChange = (index, value) => {
    const newItems = [...order.items];
    newItems[index].quantity = value;
    setOrder({ ...order, items: newItems });
  };

  if (!order) return <p>Loading...</p>;

  return (
    <div>
      <h2>Order Confirmation</h2>
      {isEditing ? (
        <>
          {order.items.map((item, index) => (
            <div key={item.itemId._id}>
              <p>{item.itemId.name} - ${item.price}</p>
              <label>Quantity:</label>
              <input 
                type="number" 
                value={item.quantity} 
                onChange={(e) => handleQuantityChange(index, e.target.value)} 
              />
            </div>
          ))}
          <label>Shipping Address:</label>
          <input type="text" value={order.shippingAddress} onChange={(e) => setOrder({ ...order, shippingAddress: e.target.value })} />
          
          <label>Billing Address:</label>
          <input type="text" value={order.billingAddress} onChange={(e) => setOrder({ ...order, billingAddress: e.target.value })} />

          <button onClick={handleSave}>Save</button>
        </>
      ) : (
        <>
          {order.items.map(item => (
            <p key={item.itemId._id}>{item.itemId.name} - ${item.price} x {item.quantity}</p>
          ))}
          <p>Shipping Address: {order.shippingAddress}</p>
          <p>Billing Address: {order.billingAddress}</p>
          <p>Total: ${order.total}</p>
          <button onClick={handleEdit}>Edit</button>
          <button onClick={handleCancel} style={{ backgroundColor: 'red', color: 'white' }}>Cancel Order</button>
          <button onClick={() => navigate('/home')}>Confirm</button>
        </>
      )}
    </div>
  );
};

export default OrderConfirmationPage;
